import React, { Component } from 'react';
import { Route, NavLink } from 'react-router-dom';
import axios from '../axios-interviews';
import { q1, q2, q3, q4, q5, q6, q7, q8, q9, q10 } from './Questions';
import { Form, Select, Input, Button, Card, Modal, Icon } from 'antd';

const FormItem = Form.Item;
const Option = Select.Option;
const { TextArea } = Input;

const colleges = ['Thapar Institute Patiala', 'LPU Jalandar','VIT Vellore',
'BITS Pilani','BIT Mesra',];



class NewInterview extends Component {
	constructor(props) {
    super(props);
		this.state = {
			name: '',
			visible: false,
			confirmLoading: false,
			ModalText: 'The form would be validated by the admins and would get live within 7 days.',
			college: '',
			company: '',
			q1: '',
			q1: '',
			q2: '',
			q3: '',
			q4: '',
			q5: '',
			q6: '',
			q7: '',
			q8: '',
			q9: '',
			q10: '',
		};
	}

	onNameChange = (event) => {
		this.setState({ name: event.target.value });
	}

  onBranchChange = (event) => {
    this.setState({ branch: event.target.value });
  }

	onCompanyChange = (event) => {
		this.setState({ company: event.target.value });
	}

	 onCollegeChange = (event) => {
    this.setState({ college: event.target.value });
  }

	onq1Change = (event) => {
		this.setState({ q1: event.target.value });
	}

	onq2Change = (event) => {
		this.setState({ q2: event.target.value });
	}

	onq3Change = (event) => {
		this.setState({ q3: event.target.value });
	}

	onq4Change = (event) => {
		this.setState({ q4: event.target.value });
	}

	onq5Change = (event) => {
		this.setState({ q5: event.target.value });
	}

	onq6Change = (event) => {
		this.setState({ q6: event.target.value });
	}

	onq7Change = (event) => {
		this.setState({ q7: event.target.value });
	}

	onq8Change = (event) => {
		this.setState({ q8: event.target.value });
	}

	onq9Change = (event) => {
		this.setState({ q9: event.target.value });
	}

	onq10Change = (event) => {
		this.setState({ q10: event.target.value });
	}

	onSubmitForm = (event) => {
		event.preventDefault();
	}

	showModal = () => {
    this.props.form.validateFields(
      (err) => {
        if (!err) {
          this.setState({
            visible: true,
          });
        }
      },
    );
  }

  handleOk = () => {
		this.setState({
		ModalText: 'Thank You for sharing your experience to others. Completing form submission',
		confirmLoading: true,
		});
		const interview = {
			name: this.state.name,
			company: this.state.company,
			college: this.state.college,
			questions: {
				q1: this.state.q1, q2: this.state.q2, q3: this.state.q3, 
				q4: this.state.q1, q5: this.state.q5, q6: this.state.q6,
				q7: this.state.q7, q8: this.state.q8, q9: this.state.q9,
				q10: this.state.q10
			}
		}
		axios.post('/interviews.json', interview)
		.then(response => {
			console.log(response);
			this.setState({
        		visible: false,
        		confirmLoading: false,
      		});
		})
		.catch(error => console.log(error.response));
  }

  handleSelectChange = (value) => {
  }

  handleCancel = () => {
    console.log('Clicked cancel button');
    this.setState({
      visible: false,
    });
  }

  render() {
    const { getFieldDecorator } = this.props.form;
  	const { visible, confirmLoading,ModalText } = this.state;
    return (
    <Card title="Add Interview Experience">
      <Form onSubmit={this.handleSubmit}>
        <FormItem required 
          label="Full Name"
          labelCol={{ span: 5 }}
          wrapperCol={{ span: 12 }}
        >
        {getFieldDecorator('userName', {
            rules: [{ required: true, message: 'Please input your Name!' }],
          })(
            <Input value={this.state.name}
        onChange={this.onNameChange} />
          )}

        </FormItem>
        <FormItem required 
          label="Your Branch"
          labelCol={{ span: 5 }}
          wrapperCol={{ span: 12 }}
        >
        {getFieldDecorator('branch', {
            rules: [{ required: true, message: 'Please input your Branch!' }],
          })(
            <Input value={this.state.branch} 
        onChange={this.onBranchChange}  /> )}
        </FormItem>
        <FormItem required 
          label="Institute"
          labelCol={{ span: 5 }}
          wrapperCol={{ span: 12 }}
        >
        {getFieldDecorator('college', {
            rules: [{ required: true, message: 'Please select your College!' }],
          })(
          <Select
            showSearch
            style={{ width: 200 }}
            placeholder="Select a person"
            optionFilterProp="children"
            onChange={handleChange}
            onFocus={handleFocus}
            onBlur={handleBlur}
            filterOption={(input, option) => option.props.children.toLowerCase().indexOf(input.toLowerCase()) >= 0}
          >
            <Option value="jack">Jack</Option>
            <Option value="lucy">Lucy</Option>
            <Option value="tom">Tom</Option>
          </Select>,
           )}
        </FormItem>
        <FormItem required
          label="Company Name"
          labelCol={{ span: 5 }}
          wrapperCol={{ span: 12 }}
        >
        {getFieldDecorator('company', {
            rules: [{ required: true, message: 'Please input your Company!' }],
          })(
            <Input value={this.state.company}
        onChange={this.onCompanyChange}  />)}
        </FormItem>
        <FormItem required
          label= {q1}
          labelCol={{ span: 5 }}
          wrapperCol={{ span: 12 }}
        >
        {getFieldDecorator('q1', {
            rules: [{ required: true, message: 'Please answer this question' }],
          })(
            <Input value={this.state.q1}
        onChange={this.onq1Change}  />)}
        </FormItem>
        <FormItem required
          label={q2}
          labelCol={{ span: 5 }}
          wrapperCol={{ span: 12 }}
        >
        {getFieldDecorator('q2', {
            rules: [{ required: true, message: 'Please answer this question' }],
          })(
            <Input value={this.state.q2}
        onChange={this.onq2Change}  />    )}
        </FormItem>
        <FormItem required
          label={q6}
          labelCol={{ span: 5, offset: 1 }}
          wrapperCol={{ span: 1 }}
        >
        {getFieldDecorator('q6', {
            rules: [{ required: true, message: 'Please answer this question' }],
          })(
            <Input value={this.state.q6}
