import React, { Component } from 'react';
import { Radio, Card, Button, Icon } from 'antd';
import  { questions } from '../Data/aptitude';
import _ from 'lodash';

const RadioGroup = Radio.Group;

const questionChoices = questions;

const tickStyle = {
  'padding': '0 2px',
};

class Quiz extends Component {
	constructor() {
    super();
    this.state = { answers: [], givenAns: [], actualAns: [] };
    this.onInput = this.onInput.bind(this);
    this.buildRadioButtons = this.buildRadioButtons.bind(this);
  }

  componentWillMount() {
    const selAns = _.range(25).map(function () { return -1 });
    this.setState({ givenAns: selAns, actualAns: selAns });
    let actualAns = selAns;
    let k = 0;

    questionChoices.forEach((obj) => {
      selAns[k] = obj.answer;
      k++;
    });
    this.setState({ selAns });
    console.log(this.state.selAns);
  }

  handleSubmit = () => {

  }

  onInput = (e) => {
    const id = e.target.name;
    const ans = e.target.value;
    const newAns = this.state.givenAns;
    const actualAns = this.state.actualAns;
    newAns[id-1] = ans;
    this.setState({ givenAns: newAns });

    console.log(this.state.givenAns);
    const answer = { id, answer: e.target.value };
    let answers;
    if (this.state.answers.some(answer => answer.id === id)) {
      answers = [...this.state.answers.filter(answer => answer.id !== id), answer];
    } else {
      answers = [...this.state.answers, answer];
    }
    this.setState({ answers }, () => console.log(this.state.answers));
  }

  buildRadioButtons(arr, type, id) {
    return arr.map((choice, i) => {
      return (
        <div key={i} value={choice} className = "righter">
          <span><Icon type="check-circle" theme="twoTone" twoToneColor="#52c41a" style = {tickSyle} /></span>
          <input
          type="radio"
          className="custom-control-input"
          name={id}
          id={choice}
          value={choice}
          onChange={this.onInput}
        />        
        <label for = {choice} onChange={this.onInput}>{choice}</label>
      </div>
     )
  })
 }

  render() {
    var iterator = questionChoices.map((question, i) => {
      const { choices, questionType, questionID, questionText } = question;
      return (
      	<div>
      		<Card>
	        	<div key={i}>
	          	<h3>{questionID}. {questionText}</h3>
	          	{this.buildRadioButtons(choices, questionType, questionID)}
	        	</div>
	        </Card>
	        <br/>
	    </div>    
      );
    });
    return (
        <div>
          <div className="form alignLeft">
            <div className="h3">
              {iterator}
            </div>
          </div>
          <Button type="primary" size="large" onClick = {this.handleSubmit}>Submit Test</Button> <br/> <br/>
        </div>
    );
  }
}

export default Quiz;
