import React, { Component } from 'react';
import axios from 'axios';
import { Card, Select } from 'antd';

const Option = Select.Option;
let obj = null;
let arr = null;

function callback(key) {
  	console.log(key);
}

class NewJobs extends Component {
	state = {
		posts: null,
		disp: null,
	}

	handleChange = () => {
		const newArr = arr.reverse();
		this.setState({ disp: newArr });
		arr = newArr;
	}

	componentWillMount() {
		axios.get('https://interview-corner-1de29.firebaseio.com/new-jobs.json')
		.then(response => {
			this.setState({ posts: response.data });
			const posts = Object.values(response.data);
		});
	}
	render() {

	let posts = null;
	if(this.state.posts) {
		console.log(Object.keys(this.state.posts));
		let k = 0;
		arr = Object.values(this.state.posts);
		if(this.state.disp) {
			posts = this.state.disp.map(post => {
				return (
					<div>
					<Card type = "inner" key = { k++ }>
						<p> { post.newPost } </p>
					</Card> <br/> <br/> <br/>
					</div>
				);
			});
		}
		else {
			posts = arr.map(post => {
				return (
					<div>
					<Card type = "inner" key = { k++ }>
						<p> { post.newPost } </p>
					</Card> <br/> <br/> <br/>
					</div>
				);
			});	
		}
	}
		return (
			<div>
		    	Sort By : <Select defaultValue="oldest" onChange={this.handleChange}>
		      				<Option value="oldest">Oldest First</Option>
		      				<Option value="newest">Newest First</Option>
		      			</Select>
				<br/> <br/>
				{posts}
			</div>
		);
	}
}

export default NewJobs;
