import React, { Component } from 'react';
import { Route, NavLink } from 'react-router-dom';
import axios from '../axios-interviews';
import { q1, q2, q3, q4, q5, q6, q7, q8, q9, q10 } from './Questions';

class NewInterview extends Component {

	state = {
		q1: '',
		email: '',
		sub: '',
		company: '',
		os: '',
		ds: '',
		hr: '',
		dbms: '',
	}

	onNameChange = (event) => {
		this.setState({ q1: event.target.value });
	}

	onCompanyChange = (event) => {
		this.setState({ company: event.target.value });
	}

	onSubChange = (event) => {
		this.setState({ sub: event.target.value });
	}

	onOsChange = (event) => {
		this.setState({ os: event.target.value });
	}

	onDsChange = (event) => {
		this.setState({ ds: event.target.value });
	}

	onDbmsChange = (event) => {
		this.setState({ dbms: event.target.value });
		console.log(this.state.dbms);
	}

	onHrChange = (event) => {
		this.setState({ hr: event.target.value });
	}

	onEmailChange = (event) => {
		this.setState({ email: event.target.value });
	}

	onSubmitForm = (event) => {
		event.preventDefault();
		const interview = {
			student: {
				name: this.state.q1,
				email: this.state.email,
			},
			company: this.state.company,
			questions: {
				os: this.state.os,
				ds: this.state.ds,
				hr: this.state.hr,
				dbms: this.state.dbms,
				sub: this.state.sub,	
			}
		}
		axios.post('/interviews.json', interview)
		.then(response => console.log(response))
		.catch(error => console.log(error.response));
	}

	render() {
	    return (
	    	<div>
	    		<div className="row">
				    <form className="col s12">
				      <div className="row">
				        <div className="input-field col s12">
				          <input id="full_name" type="text" 
				          onChange = {(event) => this.onNameChange(event)} 
				          value = {this.state.q1} />
				          <label htmlFor="full_name">Full Name</label>
				        </div>
				      </div>
				      <div className="row">
				        <div className="input-field col s6">
				          <input id="company" type="text" 
				          onChange = {(event) => this.onCompanyChange(event)}
				          />
				          <label htmlFor="company">Name Of Company</label>
				        </div>
				        <div className="input-field col s6">
				          <input id="subject" type="text"
				          onChange = {(event) => this.onSubChange(event)}
				           />
				          <label htmlFor="subject">Preferred Subject</label>
				        </div>
				      </div>
				      <div className="row">
				        <div className="input-field col s12">
				          <input id="os" type="text" 
				          onChange = {(event) => this.onOsChange(event)}
				          />
				          <label htmlFor="os">Operating System Questions</label>
				        </div>
				      </div>
				      <div className="row">
				        <div className="input-field col s12">
				          <input id="ds" type="text" 
				          onChange = {(event) => this.onDsChange(event)}
				          />
				          <label htmlFor="ds">Algorithms Questions</label>
				        </div>
				      </div>
				      <div className="row">
				        <div className="input-field col s12">
				          <input id="dbms" type="text" 
				          onChange = {(event) => this.onDbmsChange(event)}
				          />
				          <label htmlFor="dbms">DBMS Questions</label>
				        </div>
				      </div>
				      <div className="row">
				        <div className="input-field col s12">
				          <input id="hr" type="text"
				          onChange = {(event) => this.onHrChange(event)} 
				          />
				          <label htmlFor="hr">Questions asked in HR Round</label>
				        </div>
				      </div>
				      <div className="row">
				        <div className="input-field col s12">
				          <input id="email" type="email"
				          onChange = {(event) => this.onEmailChange(event)} 
				          />
				          <label htmlFor="email">Email</label>
				        </div>
				      </div>
			        <button className="btn waves-effect waves-light" type="submit" name="action"
			         onClick = {(event) => this.onSubmitForm(event)}>Submit
					</button>
				    </form>
	  			</div>			
	    	</div>
	    	);
    }
}

export default NewInterview;
