import React, { Component } from 'react';
import { Route, NavLink } from 'react-router-dom';
import axios from '../axios-interviews';
import { q1, q2, q3, q4, q5, q6, q7, q8, q9, q10 } from './Questions';
import { Form, Select, Input, Button, Card, Modal } from 'antd';

const FormItem = Form.Item;
const Option = Select.Option;
const { TextArea } = Input;

class NewInterview extends Component {

	state = {
		name: '',
		visible: false,
		confirmLoading: false,
		ModalText: 'The form would be validated by the admins and would get live within 7 days.',
		college: '',
		company: '',
		q1: '',
		q1: '',
		q2: '',
		q3: '',
		q4: '',
		q5: '',
		q6: '',
		q7: '',
		q8: '',
		q9: '',
		q10: '',
	}

	onNameChange = (event) => {
		this.setState({ name: event.target.value });
	}

	onCompanyChange = (event) => {
		this.setState({ company: event.target.value });
	}

	onCollegeChange = (event) => {
		this.setState({ college: event.target.value });
	}

	onq1Change = (event) => {
		this.setState({ q1: event.target.value });
	}

	onq2Change = (event) => {
		this.setState({ q2: event.target.value });
	}

	onq3Change = (event) => {
		this.setState({ q3: event.target.value });
	}

	onq4Change = (event) => {
		this.setState({ q4: event.target.value });
	}

	onq5Change = (event) => {
		this.setState({ q5: event.target.value });
	}

	onq6Change = (event) => {
		this.setState({ q6: event.target.value });
	}

	onq7Change = (event) => {
		this.setState({ q7: event.target.value });
	}

	onq7Change = (event) => {
		this.setState({ q1: event.target.value });
	}

	onq8Change = (event) => {
		this.setState({ q8: event.target.value });
	}

	onq9Change = (event) => {
		this.setState({ q9: event.target.value });
	}

	onq10Change = (event) => {
		this.setState({ q10: event.target.value });
	}

	onSubmitForm = (event) => {
		event.preventDefault();
	}

	showModal = () => {
    this.setState({
      visible: true,
    });
  }

  handleOk = () => {
		this.setState({
		ModalText: 'The modal will be closed after form submission',
		confirmLoading: true,
		});
		const interview = {
			name: this.state.name,
			company: this.state.company,
			college: this.state.college,
			questions: {
				q1: this.state.q1, q2: this.state.q2, q3: this.state.q3, 
				q4: this.state.q1, q5: this.state.q5, q6: this.state.q6,
				q7: this.state.q7, q8: this.state.q8, q9: this.state.q9,
				q10: this.state.q10
			}
		}
		axios.post('/interviews.json', interview)
		.then(response => {
			console.log(response);
			this.setState({
        		visible: false,
        		confirmLoading: false,
      		});
		})
		.catch(error => console.log(error.response));
  }

  handleSelectChange = (value) => {
  }

  handleCancel = () => {
    console.log('Clicked cancel button');
    this.setState({
      visible: false,
    });
  }

  render() {
  	const { visible, confirmLoading,ModalText } = this.state;
    return (
    <Card>
      <Form onSubmit={this.handleSubmit}>
        <FormItem
          label="Full Name"
          labelCol={{ span: 5 }}
          wrapperCol={{ span: 12 }}
        >
            <Input value={this.state.name}
        onChange={this.onNameChange}  />
        </FormItem>
        <FormItem
          label="Institute"
          labelCol={{ span: 5 }}
          wrapperCol={{ span: 12 }}
        >
            <Select
              placeholder="Select Institute"
              onCollegeChange={this.handleSelectChange}
            >
              <Option value="Thapar Institute Patiala">Thapar Institute Patiala</Option>
              <Option value="LPU Jalandar">LPU Jalandar</Option>
              <Option value="VIT Vellore">VIT Vellore</Option>
              <Option value="BITS Pilani">BITS Pilani</Option>
              <Option value="BIT Mesra">BIT Mesra</Option>
            </Select>
        </FormItem>
        <FormItem
          label="Company Name"
          labelCol={{ span: 5 }}
          wrapperCol={{ span: 12 }}
        >
            <Input value={this.state.name}
        onChange={this.onCompanyChange}  />
        </FormItem>
        <FormItem
          label= {q1}
          labelCol={{ span: 5 }}
          wrapperCol={{ span: 12 }}
        >
            <Input value={this.state.q1}
        onChange={this.onq1Change}  />
        </FormItem>
        <FormItem
          label={q2}
          labelCol={{ span: 5 }}
          wrapperCol={{ span: 12 }}
        >
            <Input value={this.state.q2}
        onChange={this.onq2Change}  />    
        </FormItem>
        <FormItem
          label={q6}
          labelCol={{ span: 5, offset: 1 }}
          wrapperCol={{ span: 1 }}
        >
            <Input value={this.state.q6}
        onChange={this.onq6Change}  />    
        </FormItem>
        <FormItem
          label={q3}
          labelCol={{ span: 11 }}
          wrapperCol={{ span: 16, offset: 1 }}
        >
            <TextArea value={this.state.q3}
        onChange={this.onq3Change} rows = {4} />    
        </FormItem>
        <FormItem
          label={q4}
         labelCol={{ span: 11 }}
          wrapperCol={{ span: 16, offset: 1 }}
        >
            <Input value={this.state.q4}
        onChange={this.onq1Change}  />    
        </FormItem>
        <FormItem
          label={q5}
         labelCol={{ span: 11 }}
          wrapperCol={{ span: 16, offset: 1 }}
        >
            <TextArea value={this.state.q5}
        onChange={this.onq1Change} rows = {4} />    
        </FormItem>
        <FormItem
          label={q7}
         labelCol={{ span: 11 }}
          wrapperCol={{ span: 16, offset: 1 }}
        >
            <Input value={this.state.q7}
        onChange={this.onq1Change}  />    
        </FormItem>
        <FormItem
          label={q8}
         labelCol={{ span: 11 }}
          wrapperCol={{ span: 16, offset: 1 }}
        >
            <TextArea value={this.state.q8}
        onChange={this.onq8Change} rows = {5} />    
        </FormItem>
        <FormItem
          label={q9}
         labelCol={{ span: 11 }}
          wrapperCol={{ span: 16, offset: 1 }}
        >
            <TextArea value={this.state.q9}
        onChange={this.onq1Change} rows = {4} />    
        </FormItem>
        <FormItem
          label={q10}
          labelCol={{ span: 11 }}
          wrapperCol={{ span: 16, offset: 1 }}
        >
            <Input value={this.state.q10}
        onChange={this.onq1Change}  />    
        </FormItem>
        <FormItem
          wrapperCol={{ span: 12, offset: 5 }}
        >
          <Button type="primary" onClick = {this.showModal} size="large">
            Submit
          </Button>
          <Modal title="Confirm Form Submission"
          visible={visible}
          onOk={this.handleOk}
          confirmLoading={confirmLoading}
          onCancel={this.handleCancel}
        >
        <p>{ModalText}</p>
        </Modal>
        </FormItem>
      </Form>
     </Card>
    );
  }
}

export default NewInterview;
