import React, { Component } from 'react';
import { Route, NavLink } from 'react-router-dom';
import { Icon, Input, AutoComplete, Button, Row, Col, Card } from 'antd';
import axios from 'axios';

const { Meta } = Card;

let dataSource = ['Thapar Institute Patiala', 'BIT Mesra', 'BITS Pilani', 'NIT Kurukshetra', 'LPU Jalandar', 'VIT Vellore'];
let totalImgs = null;
let x = 0;

class interviews extends Component {
	state = {
		query: '',
		filteredSrc: ['Thapar Institute Patiala', 'BIT Mesra', 'BITS Pilani', 
		'NIT Kurukshetra', 'LPU Jalandar', 'VIT Vellore'],
		imgs: null,	 
		collegeSelected: '',
		loading: true,
	}

	onQuery = (value) => {
		this.setState({ query: value });
		let newSrc = dataSource.filter(
			(src) => {
				return src.toLowerCase().indexOf(value.toLowerCase()) > -1;
			}
		);
		this.setState({ filteredSrc: newSrc });
		console.log('Search', this.state.filteredSrc);
	}

	componentWillMount() {
		axios.get('https://interview-corner-1de29.firebaseio.com/univ-imgs.json')
		.then(response => {
			this.setState({	imgs: response.data  });
		});
		console.log('imgs', this.state.imgs);
		if(this.state.imgs)
			totalImgs = this.state.imgs;
	}


	findCollege = (val) => {
		const obj = this.state.imgs;
		const college = Object.keys(obj).find(key => obj[key] == val);
		this.setState({ collegeSelected : college });
		console.log(this.state.collegeSelected);
	}

	fetchImg = () => {
		let images = null;
		if(this.state.filteredSrc && totalImgs) {
			images = this.state.filteredSrc.map(
				(src) => {
					return totalImgs[src]
				}
			);
		}
		this.setState({	imgs: images  });
		console.log(this.state.query)
	}


	render() {
		let images = null;
		if(this.state.imgs && this.state.filteredSrc) {
			if(x === 0) { 
				totalImgs = this.state.imgs;
				x = 1;
			}
			images = Object.values(this.state.imgs).map(image => {
			return (
				<div>
					<div className = "row">
					<div className="col-12 col-md-6 order-1 ">					
							<div className="card thumbnail row">
					  			<img className="card-img-top img-responsive img-rounded" src={image} />
					  			<div className="card-body">
					    			<h5 className="card-title">Card Title</h5>
					    			<p className="card-text">on the card title bulk of the card's content.</p>
					    			<button onClick = { () => this.findCollege(image) } className="btn btn-warning" >View Interviews</button>
					  			</div>
							</div>
						</div>
						</div>
					<br/>
				</div>		
				);
			});
	}

	return (
		<div>
			<br/>
			<AutoComplete
			placeholder="Select Institute" 
			onSearch = {this.onQuery}
			onSelect = {this.onQuery} 
			shape="circle" dataSource={this.state.filteredSrc} className = "rad50" >
			<Input
				suffix={(
				  <Button className="search-btn" onClick = {this.fetchImg} 
				  type="primary"><Icon type="search" /></Button>
				)}
			  />
			</AutoComplete>
			<br/> <br/>
			{images}
		</div>
		);
	}
}

export default interviews;

