import React, { Component } from 'react';
import { Route, NavLink } from 'react-router-dom';

let interviews = null;
class Interviews extends Component {

	state = {
		selectedInterviews: null
	}

	componentDidMount() {
		axios.get('https://interview-corner-1de29.firebaseio.com/interviews.json').
		then(response => 
			interviews = response.data);
		if(interviews) {
			let filteredInterviews = interviews.filter(
				(interview) => {
					return interview.college === selectedCollege;
				}
			);
			this.setState(selectedInterviews : filteredInterviews);	
		}
	}

	render() {
	    return (
			<div></div>
	    );
    }
}

export default Interviews;
