import React, { Component } from 'react';
import { Radio, Card } from 'antd';
import  { data } from '../Data/data';
import _ from 'lodash';

const RadioGroup = Radio.Group;

class Quiz extends Component {
	state = {
		data: null,
		selectedAns: null,
	}

	componentWillMount() {
		let shuffledQues = _.shuffle(data);	
		this.setState({ data: shuffledQues.splice(0, 3) });
		let selectedAns = _.range(3).map(function() {return -1 });
		this.setState(selectedAns: selectedAns);
	}

	onChange = (e) => {
	    console.log('radio checked', e.target.value);
	    this.setState({
	    	value: e.target.value,
	    });
	}

	render() {	
		console.log('data', this.state.data);
		let displayData = null;
		if(this.state.data) {
			displayData = this.state.data.map(element => {
				return (
					<div>
					<Card className = "notFull">
						<p>{element[0]}</p>
						<RadioGroup key={element[0]} onChange={(event) => this.onChange(event)} value={this.state.value}>
					        <Radio value={1}>{element[1]}</Radio>
					        <Radio value={2}>{element[2]}</Radio>
					        <Radio value={3}>{element[3]}</Radio>
					    	<Radio value={4}>{element[4]}</Radio>
					    </RadioGroup>
					</Card>
					<br/> <br/>
					</div>
				)    
			});
			return (
	            <div>{displayData}</div>
	        );
    	}
	}
}

export default Quiz;
