import React, { Component } from 'react';
import { Route, NavLink } from 'react-router-dom';
import axios from '../axios-interviews';
import { q1, q2, q3, q4, q5, q6, q7, q8, q9, q10 } from './Questions';
import { Form, Select, Input, Button } from 'antd';

const FormItem = Form.Item;
const Option = Select.Option;
const { TextArea } = Input;

class NewInterview extends Component {

	state = {
		name: '',
		college: '',
		company: '',
		q1: '',
		q1: '',
		q2: '',
		q3: '',
		q4: '',
		q5: '',
		q6: '',
		q7: '',
		q8: '',
		q9: '',
		q10: '',
	}

	onNameChange = (event) => {
		this.setState({ name: event.target.value });
	}

	onCompanyChange = (event) => {
		this.setState({ company: event.target.value });
	}

	onCollegeChange = (event) => {
		this.setState({ college: event.target.value });
	}

	onq1Change = (event) => {
		this.setState({ q1: event.target.value });
	}

	onq2Change = (event) => {
		this.setState({ q2: event.target.value });
	}

	onq3Change = (event) => {
		this.setState({ q3: event.target.value });
	}

	onq4Change = (event) => {
		this.setState({ q4: event.target.value });
	}

	onq5Change = (event) => {
		this.setState({ q5: event.target.value });
	}

	onq6Change = (event) => {
		this.setState({ q6: event.target.value });
	}

	onq7Change = (event) => {
		this.setState({ q7: event.target.value });
	}

	onq7Change = (event) => {
		this.setState({ q1: event.target.value });
	}

	onq8Change = (event) => {
		this.setState({ q8: event.target.value });
	}

	onq9Change = (event) => {
		this.setState({ q9: event.target.value });
	}

	onq10Change = (event) => {
		this.setState({ q10: event.target.value });
	}

	onSubmitForm = (event) => {
		event.preventDefault();
		const interview = {
			student: {
				name: this.state.q1,
				email: this.state.email,
			},
			company: this.state.company,
			questions: {
				os: this.state.os,
				ds: this.state.ds,
				hr: this.state.hr,
				dbms: this.state.dbms,
				sub: this.state.sub,	
			}
		}
		axios.post('/interviews.json', interview)
		.then(response => console.log(response))
		.catch(error => console.log(error.response));
	}

	handleSubmit = (e) => {
    e.preventDefault();
    this.props.form.validateFields((err, values) => {
      if (!err) {
        console.log('Received values of form: ', values);
      }
    });
  }

  handleSelectChange = (value) => {
    console.log(value);
    this.props.form.setFieldsValue({
      note: `Hi, ${value === 'male' ? 'man' : 'lady'}!`,
    });
  }

  render() {
    return (
      <Form onSubmit={this.handleSubmit}>
        <FormItem
          label="Full Name"
          labelCol={{ span: 5 }}
          wrapperCol={{ span: 12 }}
        >
            <Input />
        </FormItem>
        <FormItem
          label="Institute"
          labelCol={{ span: 5 }}
          wrapperCol={{ span: 12 }}
        >
            <Select
              placeholder="Select Institute"
              onChange={this.handleSelectChange}
            >
              <Option value="Thapar Institute Patiala">Thapar Institute Patiala</Option>
              <Option value="LPU Jalandar">LPU Jalandar</Option>
              <Option value="VIT Vellore">VIT Vellore</Option>
              <Option value="BITS Pilani">BITS Pilani</Option>
              <Option value="BIT Mesra">BIT Mesra</Option>
            </Select>
        </FormItem>
        <FormItem
          label="Company Name"
          labelCol={{ span: 5 }}
          wrapperCol={{ span: 12 }}
        >
            <Input />
        </FormItem>
        <FormItem
          label="Full Name"
          labelCol={{ span: 5 }}
          wrapperCol={{ span: 12 }}
        >
            <Input />
        </FormItem>
        <FormItem
          label= {q1}
          labelCol={{ span: 5 }}
          wrapperCol={{ span: 12 }}
        >
            <Input />
        </FormItem>
        <FormItem
          label={q2}
          labelCol={{ span: 5 }}
          wrapperCol={{ span: 12 }}
        >
            <Input />    
        </FormItem>
        <FormItem
          label={q6}
          labelCol={{ span: 5 }}
          wrapperCol={{ span: 1 }}
        >
            <Input />    
        </FormItem>
        <FormItem
          label={q3}
          labelCol={{ span: 15 }}
          wrapperCol={{ span: 15 }}
        >
            <Input />    
        </FormItem>
        <FormItem
          label={q4}
          labelCol={{ span: 15 }}
          wrapperCol={{ span: 15 }}
        >
            <Input />    
        </FormItem>
        <FormItem
          label={q5}
          labelCol={{ span: 15 }}
          wrapperCol={{ span: 15 }}
        >
            <Input />    
        </FormItem>
        <FormItem
          label={q7}
          labelCol={{ span: 15 }}
          wrapperCol={{ span: 15 }}
        >
            <Input />    
        </FormItem>
        <FormItem
          label={q8}
          labelCol={{ span: 15 }}
          wrapperCol={{ span: 15 }}
        >
            <Input />    
        </FormItem>
        <FormItem
          label={q9}
          labelCol={{ span: 15 }}
          wrapperCol={{ span: 15 }}
        >
            <Input />    
        </FormItem>
        <FormItem
          label={q10}
          labelCol={{ span: 15 }}
          wrapperCol={{ span: 12, offset: 4 }}
        >
            <Input />    
        </FormItem>
        <FormItem
          wrapperCol={{ span: 12, offset: 5 }}
        >
          <Button type="primary" htmlType="submit">
            Submit
          </Button>
        </FormItem>
      </Form>
    );
  }
}

export default NewInterview;
