import React, { Component } from 'react';
import { Route, NavLink } from 'react-router-dom';
import axios from '../axios-interviews';
import { q1, q2, q3, q4, q5, q6, q7, q8, q9, q10 } from './Questions';
import { Form, Input, Tooltip, Icon, Cascader, Select, Row, Col, Checkbox, Button, AutoComplete } from 'antd';


class NewInterview extends Component {

	state = {
		name: '',
		college: '',
		company: '',
		q1: '',
		q1: '',
		q2: '',
		q3: '',
		q4: '',
		q5: '',
		q6: '',
		q7: '',
		q8: '',
		q9: '',
		q10: '',
	}

	onNameChange = (event) => {
		this.setState({ name: event.target.value });
	}

	onCompanyChange = (event) => {
		this.setState({ company: event.target.value });
	}

	onCollegeChange = (event) => {
		this.setState({ college: event.target.value });
	}

	onq1Change = (event) => {
		this.setState({ q1: event.target.value });
	}

	onq2Change = (event) => {
		this.setState({ q2: event.target.value });
	}

	onq3Change = (event) => {
		this.setState({ q3: event.target.value });
	}

	onq4Change = (event) => {
		this.setState({ q4: event.target.value });
	}

	onq5Change = (event) => {
		this.setState({ q5: event.target.value });
	}

	onq6Change = (event) => {
		this.setState({ q6: event.target.value });
	}

	onq7Change = (event) => {
		this.setState({ q7: event.target.value });
	}

	onq7Change = (event) => {
		this.setState({ q1: event.target.value });
	}

	onq8Change = (event) => {
		this.setState({ q8: event.target.value });
	}

	onq9Change = (event) => {
		this.setState({ q9: event.target.value });
	}

	onq10Change = (event) => {
		this.setState({ q9: event.target.value });
	}

	onSubmitForm = (event) => {
		event.preventDefault();
		const interview = {
			student: {
				name: this.state.q1,
				email: this.state.email,
			},
			company: this.state.company,
			questions: {
				os: this.state.os,
				ds: this.state.ds,
				hr: this.state.hr,
				dbms: this.state.dbms,
				sub: this.state.sub,	
			}
		}
		axios.post('/interviews.json', interview)
		.then(response => console.log(response))
		.catch(error => console.log(error.response));
	}

	

	  render() {
    const { getFieldDecorator } = this.props.form;
    const { autoCompleteResult } = this.state;

    const formItemLayout = {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 8 },
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 16 },
      },
    };
    const tailFormItemLayout = {
      wrapperCol: {
        xs: {
          span: 24,
          offset: 0,
        },
        sm: {
          span: 16,
          offset: 8,
        },
      },
    };
    const prefixSelector = getFieldDecorator('prefix', {
      initialValue: '86',
    })(
      <Select style={{ width: 70 }}>
        <Option value="86">+86</Option>
        <Option value="87">+87</Option>
      </Select>
    );

    const websiteOptions = autoCompleteResult.map(website => (
      <AutoCompleteOption key={website}>{website}</AutoCompleteOption>
    ));

    return (
      <Form onSubmit={this.handleSubmit}>
        <FormItem
          {...formItemLayout}
          label="E-mail"
        >
          {getFieldDecorator('email', {
            rules: [{
              type: 'email', message: 'The input is not valid E-mail!',
            }, {
              required: true, message: 'Please input your E-mail!',
            }],
          })(
            <Input />
          )}
        </FormItem>
        <FormItem
          {...formItemLayout}
          label="Password"
        >
          {getFieldDecorator('password', {
            rules: [{
              required: true, message: 'Please input your password!',
            }, {
              validator: this.validateToNextPassword,
            }],
          })(
            <Input type="password" />
          )}
        </FormItem>
        <FormItem
          {...formItemLayout}
          label="Confirm Password"
        >
          {getFieldDecorator('confirm', {
            rules: [{
              required: true, message: 'Please confirm your password!',
            }, {
              validator: this.compareToFirstPassword,
            }],
          })(
            <Input type="password" onBlur={this.handleConfirmBlur} />
          )}
        </FormItem>
        <FormItem
          {...formItemLayout}
          label={(
            <span>
              Nickname&nbsp;
              <Tooltip title="What do you want others to call you?">
                <Icon type="question-circle-o" />
              </Tooltip>
            </span>
          )}
        >
          {getFieldDecorator('nickname', {
            rules: [{ required: true, message: 'Please input your nickname!', whitespace: true }],
          })(
            <Input />
          )}
        </FormItem>
        <FormItem
          {...formItemLayout}
          label="Habitual Residence"
        >
          {getFieldDecorator('residence', {
            initialValue: ['zhejiang', 'hangzhou', 'xihu'],
            rules: [{ type: 'array', required: true, message: 'Please select your habitual residence!' }],
          })(
            <Cascader options={residences} />
          )}
        </FormItem>
        <FormItem
          {...formItemLayout}
          label="Phone Number"
        >
          {getFieldDecorator('phone', {
            rules: [{ required: true, message: 'Please input your phone number!' }],
          })(
            <Input addonBefore={prefixSelector} style={{ width: '100%' }} />
          )}
        </FormItem>
        <FormItem
          {...formItemLayout}
          label="Website"
        >
          {getFieldDecorator('website', {
            rules: [{ required: true, message: 'Please input website!' }],
          })(
            <AutoComplete
              dataSource={websiteOptions}
              onChange={this.handleWebsiteChange}
              placeholder="website"
            >
              <Input />
            </AutoComplete>
          )}
        </FormItem>
        <FormItem
          {...formItemLayout}
          label="Captcha"
          extra="We must make sure that your are a human."
        >
          <Row gutter={8}>
            <Col span={12}>
              {getFieldDecorator('captcha', {
                rules: [{ required: true, message: 'Please input the captcha you got!' }],
              })(
                <Input />
              )}
            </Col>
            <Col span={12}>
              <Button>Get captcha</Button>
            </Col>
          </Row>
        </FormItem>
        <FormItem {...tailFormItemLayout}>
          {getFieldDecorator('agreement', {
            valuePropName: 'checked',
          })(
            <Checkbox>I have read the <a href="">agreement</a></Checkbox>
          )}
        </FormItem>
        <FormItem {...tailFormItemLayout}>
          <Button type="primary" htmlType="submit">Register</Button>
        </FormItem>
      </Form>
    );
  }






}

export default NewInterview;
